import React, { useEffect, useRef, useState }  from 'react';
import { connect } from 'react-redux';
import Isotope from 'isotope-layout'


import CollectionItem from '../../components/collection-item/collection-item.component';
import { selectCollection } from '../../redux/shop/shop.selectors';

import {
    CollectionPageContainer,
    CollectionTitle,
    CollectionItemsContainer,

    Row,
    Col
  } from './collection.styles';

const CollectionPage = ({ collection }) => {
console.log(collection)
    const { title, items } = collection;
    const isoRef = useRef()
    const [state, setState] = useState({ isotope: null })

    useEffect(() => {
        if (!state.isotope) {
            setState({
                isotope: new Isotope( isoRef.current )
            })
        } else if (state.isotope) {
            state.isotope.reloadItems()
        } 
    })

    return (
        <Row>
            <ul>
                <li onClick={() => setState("*")}>Show Both</li>
                <li onClick={() => setState({title})}>Show {title}</li>
                <li onClick={() => setState("fruit")}>Show Fruits</li>
            </ul>
            <Col>
                {/* <div ref={isoRef}>
                    {items.map(item => (
                        <CollectionItem 
                            className={`filter-item ${title}`}
                            key={item.id} 
                            item={item} 
                        />
                    ))}
                </div> */}
                <div className="grid">
                    <div className="grid-item tech">Item 1</div>
                    <div className="grid-item tech">Item 2</div>
                    <div className="grid-item personal">Item 3</div>
                    <div className="grid-item personal">Item 4</div>
                </div>
            </Col>
        </Row>
    );

    // return (
    //     <CollectionPageContainer>
    //         <CollectionTitle>{title}</CollectionTitle>
    //         <CollectionItemsContainer>
    //             {items.map(item => (
    //                 <CollectionItem 
    //                     key={item.id} 
    //                     item={item} 
    //                 />
    //             ))}
    //         </CollectionItemsContainer>
    //     </CollectionPageContainer>
    // );
};

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
});

export default connect(
    mapStateToProps
)(CollectionPage);
