import styled from 'styled-components';

export const NewsletterContainer = styled.div`
    width: 100%;
	background: #FFFFFF;
	padding-bottom: 99px;
	z-index: 2;
`;

export const Container = styled.div`
    width: 100%;
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;

    @media (min-width: 576px) {
        max-width: 540px;
    }    
    @media (min-width: 768px) {
        max-width: 720px;
    }
    @media (min-width: 992px) {
        max-width: 960px;
    }
    @media only screen and (max-width: 1199px)
    {
        max-width: 100%;
    }
    @media (min-width: 1200px) {
        max-width: 1170px;
    }
`;

export const Row = styled.div`
    display: flex;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    margin-right: -15px;
    margin-left: -15px;
`;

export const Col = styled.div`
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    -ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    position: relative;
    width: 100%;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
`;

export const NewsletterBorder = styled.div`
    width: 100%;
	border-top: solid 2px #e3e3e3;
`;

export const NewsletterCol = styled.div`
    position: relative;
    width: 100%;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;

    @media (min-width: 992px) {
        margin-left: 16.666667%;
        -ms-flex: 0 0 66.666667%;
        flex: 0 0 66.666667%;
        max-width: 66.666667%;
    }
`;

export const NewsletterContent = styled.div`
    padding-top: 88px;
`;

export const NewsletterTitle = styled.div`
    font-size: 30px;
	font-weight: 500;
    color: #1b1b1b;
    
    @media only screen and (max-width: 575px) {
		font-size: 24px;
	}
`;
