import React, { useState } from 'react'
import { MainContainer, SliderContainer, Card, Img, ArrowLeft, ArrowRight, ContentContainer, Container, Row, Col, Content } from './carousel.styles'

import Slide from '@material-ui/core/Slide';
import Img1 from '../../assets/home_slider_1.jpg'
import Img2 from '../../assets/avds_small.jpg'


const Carousel = () => {

    const SLIDE_INFO = [
        { img: Img1, title: 'Slide 1' },
        { img: Img2, title: 'Slide 2' }
    ];
    const [index, setIndex] = useState(0);
    const content = SLIDE_INFO[index];
    const numSlides = SLIDE_INFO.length;

    const { img, title } = content;

    const [slideIn, setSlideIn] = useState(true);
    const [slideDirection, setSlideDirection] = useState('down');

    const onArrowClick = (direction) => {
        const increment = direction === 'left' ? -1 : 1;
        const newIndex = (index + increment + numSlides) % numSlides;

        const oppDirection = direction === 'left' ? 'right' : 'left';
        setSlideDirection(direction);
        setSlideIn(false);

        setTimeout(() => {
            setIndex(newIndex);
            setSlideDirection(oppDirection);
            setSlideIn(true);
        }, 500);
    };

    return (
        <MainContainer>
            
            <SliderContainer>
        
                <ArrowLeft
                    onClick={() => onArrowClick('left')}
                />
                <Slide in={slideIn} direction={slideDirection}>
                    <Card img={img} className={img}>
                        <Img img={img} />
                        <ContentContainer>
                            <Container>
                                <Row>
                                    <Col>
                                        <Content>
                                            <div className="home_slider_title">A new Online Shop experience.</div>
                                            <div className="home_slider_subtitle">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam a ultricies metus. Sed nec molestie eros. Sed viverra velit venenatis fermentum luctus.</div>
                                            <div className="button button_light home_button"><a href="#">Shop Now</a></div>
                                        </Content>
                                    </Col>
                                </Row>
                            </Container>
                        </ContentContainer>
                    </Card>                    
                </Slide>
                <ArrowRight
                    onClick={() => onArrowClick('right')}
                />

            </SliderContainer>

        </MainContainer>        
    );
}

export default Carousel
