import React from 'react';
import { connect } from 'react-redux';

import { createStructuredSelector } from 'reselect';

import { selectCartHidden} from '../../redux/cart/cart.selectors';
import { selectCurrentUser } from '../../redux/user/user.selectors';
import { signOutStart } from '../../redux/user/user.actions';
import { Link } from 'react-router-dom';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import { ReactComponent as Logo } from '../../assets/crown.svg';

import { HeaderMain, HeaderContainer, Container, Row, Col, HeaderContent, LogoContainer, Nav } from './headers.styles';

const Headers = ({ currentUser, hidden, signOutStart }) => (
    <HeaderMain>
		<HeaderContainer>
			<Container>
				<Row>
					<Col>
						<HeaderContent>
                            <LogoContainer to='/'><Logo /></LogoContainer>
							<Nav>
								<ul>
									<li className="active"><Link to='/'>Home</Link></li>
                                    <li className="hassubs">
                                        <Link to='/shop'>Shop</Link>
                                        <ul>
                                            <li><a href="categories.html">Category</a></li>
                                            <li><a href="categories.html">Category</a></li>
                                            <li><a href="categories.html">Category</a></li>
                                            <li><a href="categories.html">Category</a></li>
                                            <li><a href="categories.html">Category</a></li>
                                        </ul>
                                    </li>
                                    <li><Link to='/contact'>Contact</Link></li>
                                </ul>
                            </Nav>
                            <div className="header_extra ml-auto">
								<div className="shopping_cart">
                                    <CartIcon />
								</div>
								<div className="search">
									<div className="search_icon">
							
									</div>
								</div>
								<div className="hamburger"><i className="fa fa-bars" aria-hidden="true"></i></div>
							</div>
                        </HeaderContent>
                        {
                            hidden ? null : <CartDropdown />
                        }   
                    </Col>
                </Row>
            </Container>
        </HeaderContainer>
    </HeaderMain>
);

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    hidden: selectCartHidden
});

const mapDispatchToProps = dispatch => ({
    signOutStart: () => dispatch(signOutStart())
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Headers);
