import React, { useEffect, useRef, useState }  from 'react';
import { connect } from 'react-redux';
import Isotope from 'isotope-layout'


import CollectionItem from '../../components/collection-item/collection-item.component';
import { selectCollection } from '../../redux/shop/shop.selectors';

import {
    CollectionPageContainer,
    CollectionTitle,
    CollectionItemsContainer,

    Row,
    Col
  } from './collection.styles';

const CollectionPage = ({ collection }) => {
console.log(collection)
    const { title, items } = collection;
    const isoRef = useRef()
     // store the isotope object in one state
    const [isotope, setIsotope] = React.useState(null);
    const [state, setState] = useState({ isotope: null })
    // store the filter keyword in another state
    const [filterKey, setFilterKey] = React.useState("*");
    const [sortKey, setSortKey] = React.useState("number");

    // initialize an Isotope object with configs
    React.useEffect(() => {
        if (!state.isotope) {
            const curr = new Isotope(".filter-container", {
                itemSelector: ".filter-item",
                layoutMode: "fitRows",
                getSortData:
                    {
                        number: '.number parseInt',
                        name: '.name',
                    },
            })
            setState({
                isotope: new Isotope( curr )
            })
        } else if (state.isotope) {
            state.isotope.reloadItems()
        } 
    }, []);

    React.useEffect(
        () => {
          if (isotope) {
            filterKey === "*"
              ? isotope.arrange({ filter: `*` , sortBy: sortKey})
            : isotope.arrange({ filter: `.${filterKey}` , sortBy: sortKey});
          }
        },
        [isotope, filterKey, sortKey]
      );


      return (
        <>
          <ul>
            <li onClick={() => setFilterKey("*")}>Show Both</li>
            <li onClick={() => setFilterKey("vege")}>Show Veges</li>
            <li onClick={() => setFilterKey("fruit")}>Show Fruits</li>
            <li onClick={() => setSortKey("number")}>Sort Number</li>
            <li onClick={() => setSortKey("name")}>Sort Name</li>
          </ul>
          <hr />
          <ul ref={isoRef} className="filter-container">
            <div className="filter-item vege">
              <span className="name">Cucumber</span>
              <p className="number">145</p>
            </div>
            <div className="filter-item fruit">
              <span className="name">Apple</span>
              <p className="number">50</p>
            </div>
            <div className="filter-item fruit">
              <span className="name">Orange</span>
              <p className="number">1322</p>
            </div>
            <div className="filter-item fruit vege">
              <span className="name">Tomato</span>
              <p className="number">14</p>
            </div>
          </ul>
        </>
      
    );

    // return (
    //     <CollectionPageContainer>
    //         <CollectionTitle>{title}</CollectionTitle>
    //         <CollectionItemsContainer>
    //             {items.map(item => (
    //                 <CollectionItem 
    //                     key={item.id} 
    //                     item={item} 
    //                 />
    //             ))}
    //         </CollectionItemsContainer>
    //     </CollectionPageContainer>
    // );
};

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
});

export default connect(
    mapStateToProps
)(CollectionPage);
