import UserActionTypes from './user.types';
import { auth, googleProvider, createUserProfileDocument, getCurrentUser } from '../../firebase/firebase.utils';

export const googleSignInStart = () => ({
    type: UserActionTypes.GOOGLE_SIGN_IN_START
});

export const signInSuccess = user => ({
    type: UserActionTypes.SIGN_IN_SUCCESS,
    payload: user
});

export const signInFailure = error => ({
    type: UserActionTypes.SIGN_IN_FAILURE,
    payload: error
});

export const emailSignInStart = emailAndPassword => ({
    type: UserActionTypes.EMAIL_SIGN_IN_START,
    payload: emailAndPassword
});

export const getSnapshotFromUserAuth = (userAuth, additionalData) => async dispatch => {
    console.log('asdf')
        try {
            const userRef = createUserProfileDocument(userAuth, additionalData);
            console.log(userRef)
            const userSnapShot = userRef.get();
            dispatch(signInSuccess({
                id: userSnapShot.id,
                ...userSnapShot.data()
            }));
        } catch(error) {
            dispatch(signInFailure(error))
        }
}

export const emailSignInAsync = ({ email, password }) => async dispatch =>{
     
    dispatch(emailSignInStart());
    auth.signInWithEmailAndPassword(email, password)

    .then(user => {
        dispatch(signInSuccess({
            id: user.id,
            ...user.data()
        }));
    })
    .catch(error => {
      //Do something with the error if you want!
      dispatch(signInFailure(error));
    });

};

export const checkUserSession = () => ({
    type: UserActionTypes.CHECK_USER_SESSION
});

export const signOutStart = () => ({
    type: UserActionTypes.SIGN_OUT_START
});

export const signOutSuccess = () => ({
    type: UserActionTypes.SIGN_OUT_SUCCESS
});

export const signOutFailure = error => ({
    type: UserActionTypes.SIGN_OUT_FAILURE,
    payload: error
});

export const signUpStart = (userCredentials) => ({
    type: UserActionTypes.SIGN_UP_START,
    payload: userCredentials
});

export const signUpSuccess = ({ user, additionalData }) => ({
    type: UserActionTypes.SIGN_UP_SUCCESS,
    payload: {
        user, 
        additionalData
    }
});

export const signUpFailure = error => ({
    type: UserActionTypes.SIGN_UP_FAILURE,
    payload: error
});
