import React from 'react'
import { makeStyles } from '@material-ui/core'
import styled from 'styled-components'
import { Card, Img } from './carrousel.styles'
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';

const ArrowLeft = styled(FaChevronLeft)`
    
`

const ArrowRight = styled(FaChevronRight)`

`

const Carousel = () => {

    const SLIDE_INFO = [
        { img: Img1, title: 'Slide 1' },
        { img: Img2, title: 'Slide 2' }
    ];
    const [index, setIndex] = useState(0);
    const content = SLIDE_INFO[index];
    const numSlides = SLIDE_INFO.length;

    const { img, title } = content;

    const [slideIn, setSlideIn] = useState(true);
    const [slideDirection, setSlideDirection] = useState('down');

    const onArrowClick = (direction) => {
        const increment = direction === 'left' ? -1 : 1;
        const newIndex = (index + increment + numSlides) % numSlides;

        const oppDirection = direction === 'left' ? 'right' : 'left';
        setSlideDirection(direction);
        setSlideIn(false);

        setTimeout(() => {
            setIndex(newIndex);
            setSlideDirection(oppDirection);
            setSlideIn(true);
        }, 500);
    };

    return (
        <>
            <ArrowLeft
                onClick={() => onArrowClick('left')}
            />
            <Slide in={slideIn} direction={slideDirection}>
                <Card img={img} className={img}>
                    <Img img={img} />
                </Card>
            </Slide>
            <ArrowRigth
                onClick={() => onArrowClick('right')}
            />
        </>        
    );
}

export default Carousel
