import React, { useState } from 'react'
import { 
    MainContainer, SliderContainer, Card, Img, ArrowLeft, ArrowRight, ContentContainer, 
    Container, Row, Col, Content, Title, Subtitle, Button,
    DotsContainer
 } from './carousel.styles'

import Slide from '@material-ui/core/Slide';
import Img1 from '../../assets/home_slider_1.jpg'
import Img2 from '../../assets/avds_small.jpg'


const Carousel = () => {

    const SLIDE_INFO = [
        { img: Img1, title: 'Slide 1' },
        { img: Img2, title: 'Slide 2' }
    ];
    const [index, setIndex] = useState(0);
    const content = SLIDE_INFO[index];
    const numSlides = SLIDE_INFO.length;

    const { img, title } = content;

    const [slideIn, setSlideIn] = useState(true);
    const [slideDirection, setSlideDirection] = useState('down');

    const onArrowClick = (direction) => {
        const increment = direction === 'left' ? -1 : 1;
        const newIndex = (index + increment + numSlides) % numSlides;

        const oppDirection = direction === 'left' ? 'right' : 'left';
        setSlideDirection(direction);
        setSlideIn(false);

        setTimeout(() => {
            setIndex(newIndex);
            setSlideDirection(oppDirection);
            setSlideIn(true);
        }, 500);
    };

    return (
        <MainContainer>
            
            <SliderContainer>
        
                <ArrowLeft
                    onClick={() => onArrowClick('left')}
                />
                <Slide in={slideIn} direction={slideDirection}>
                    <Card img={img} className={img}>
                        <Img img={img} />
                        <ContentContainer>
                            <Container>
                                <Row>
                                    <Col>
                                        <Content>
                                            <Title>A new Online Shop experience.</Title>
                                            <Subtitle>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam a ultricies metus. Sed nec molestie eros. Sed viverra velit venenatis fermentum luctus.</Subtitle>
                                            <Button><a href="#">Shop Now</a></Button>
                                        </Content>
                                    </Col>
                                </Row>
                            </Container>
                        </ContentContainer>
                        <DotsContainer>
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <div className="home_slider_dots">
                                            <ul id="home_slider_custom_dots" className="home_slider_custom_dots">
                                                <li className="home_slider_custom_dot active">01.</li>
                                                <li className="home_slider_custom_dot">02.</li>
                                                <li className="home_slider_custom_dot">03.</li>
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            </div>	
                        </DotsContainer>
                    </Card>                    
                </Slide>
                <ArrowRight
                    onClick={() => onArrowClick('right')}
                />

            </SliderContainer>

        </MainContainer>        
    );
}

export default Carousel
